float dist(vec2 p) {
    float min_d = 0.;
    float t = -iTime;
    for (float i=0.;i<5.;i+=1.) {
        float a = 3.1415926535/5.*i*2.+t;
        min_d += 1./length(p+vec2(sin(a), cos(a))*.5);
    }
    return min_d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-iResolution.xy/2.0)/iResolution.y*2.;

    float d = dist(uv)/3.+1./length(uv+vec2(sin(iTime),cos(iTime))*.5);
    float v = sign(d-6.);
    fragColor = vec4(vec3(v),1.0);
}
