struct uvp {
    vec2 uv;
    float mul;
};

uvp d(vec2 uv) {
    float s = (0.125*sin(((uv.x*1.0)+iTime)*3.141));
    s *= length(uv.x-0.5);
    vec2 v = vec2(uv.x, uv.y+s);
    vec2 m = step(abs(v*2.0), vec2(1.0, 0.5));
    return uvp(v, m.x*m.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy-(0.5*iResolution.xy))/iResolution.y;

    vec4 col = vec4(0.0);
    uvp v = d(uv);
    col += vec4(0.4, 0.12, 0.11, 0.0) * v.mul;
    col += texture(iChannel0, v.uv).x * v.mul * 0.25;


    fragColor = col;
}
