void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Scenes
    vec4 scene1 = texture(iChannel0, uv); 
    vec4 scene2 = texture(iChannel1, uv); 

    // Shape
    float strength = min(2.0 * fract(iTime * 0.05), 1.0);
    vec2 p = iResolution.xy * 0.5;  
    vec2 c = iResolution.xy * uv;  
    float r = iResolution.y * strength;
    float circle = (length(p - c) - r) / iResolution.y;

    // Mask
    float mask = smoothstep(0.0, 0.1, circle);

    // Output to screen
    fragColor = scene1 * mask + scene2 * (1.0 - mask);
}
