renderpass.code:
float lum(vec3 c)
{
    return (c.x + c.y + c.z) / 3.;
    //return 0.2126*c.r + 0.7152*c.g + 0.0722*c.b;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 texelsize = 1. / iResolution.xy;

    float dx = 0.;
    float dy = 0.;

    dx -= lum(texture(iChannel0, vec2(uv.x - texelsize.x, uv.y - texelsize.y)).rgb) * 1.0;
	dx -= lum(texture(iChannel0, vec2(uv.x - texelsize.x, uv.y              )).rgb) * 2.0;
	dx -= lum(texture(iChannel0, vec2(uv.x - texelsize.x, uv.y + texelsize.y)).rgb) * 1.0;
	dx += lum(texture(iChannel0, vec2(uv.x + texelsize.x, uv.y - texelsize.y)).rgb) * 1.0;
	dx += lum(texture(iChannel0, vec2(uv.x + texelsize.x, uv.y              )).rgb) * 2.0;
	dx += lum(texture(iChannel0, vec2(uv.x + texelsize.x, uv.y + texelsize.y)).rgb) * 1.0;

    dy -= lum(texture(iChannel0, vec2(uv.x - texelsize.x, uv.y - texelsize.y)).rgb) * 1.0;
	dy -= lum(texture(iChannel0, vec2(uv.x              , uv.y - texelsize.y)).rgb) * 2.0;
	dy -= lum(texture(iChannel0, vec2(uv.x + texelsize.x, uv.y - texelsize.y)).rgb) * 1.0;
	dy += lum(texture(iChannel0, vec2(uv.x - texelsize.x, uv.y + texelsize.y)).rgb) * 1.0;
	dy += lum(texture(iChannel0, vec2(uv.x              , uv.y + texelsize.y)).rgb) * 2.0;
	dy += lum(texture(iChannel0, vec2(uv.x + texelsize.x, uv.y + texelsize.y)).rgb) * 1.0;

    float nx = dx;
    float ny = dy;

    vec3 norm = vec3(nx,
                     ny,
                    sqrt(1.0 - nx*nx - ny*ny));

    fragColor = vec4(norm * vec3(0.5, 0.5, 1.0) + vec3(0.5, 0.5, 0.0), 1.0);
}
