void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Settings!
    vec3 outerColor = vec3(85.0 / 255.0, 205.0 / 255.0, 252.0 / 255.0);
    vec3 innerColor = vec3(247.0 / 255.0, 168.0 / 255.0, 184.0 / 255.0);
    vec3 centerColor = vec3(1.0, 1.0, 1.0);
    float waveSpeed = 0.65 + sin(mod(iTime, 5.75)) * 2.13;
    float waveYScale = 0.075 + sin(iTime * 5.0) * 0.055;
    float waveXScale = 13.0 + tan(iTime * 0.825) * 7.0;
    float waveBlinkRate = 4.75;
    float innerWaveXOffset = 16.0 + sin(iTime * 2.5) * 25.0;
    float noiseScrollSpeed = 0.85;
    float noiseResolutionScale = 0.24 + sin(mod(iTime, 2.35)) * 0.361;

    vec3 color;

    vec2 xy = fragCoord / iResolution.xy;
    float noise = texture(iChannel0, fragCoord / (iResolution.x * noiseResolutionScale) + iTime * noiseScrollSpeed).r;
    float wave = sin(xy.x * waveXScale) * cos(tan(iTime) * waveSpeed + noise) * waveYScale;
    float innerWave = sin(xy.x * waveXScale + innerWaveXOffset) * cos(sin(iTime) * waveSpeed + noise) * waveYScale;
    float waveBlink = cos(iTime * waveBlinkRate);
    float distanceFromCenter = abs(0.5 - xy.y);
    if (distanceFromCenter < 0.1 + wave && waveBlink * waveBlink >= abs(0.5 - xy.y + wave) * noise)
    {
        color = centerColor;
    }
    else if (distanceFromCenter < 0.3 + innerWave * noise * 0.5)
    {
        color = innerColor;
    }
    else
    {  
		color = (distanceFromCenter * (noise * wave + 0.5) > 0.25) ? innerColor : outerColor; 
        color = (noise * (wave + 0.5) > 0.025) ? color : centerColor;
        color = (noise * (wave + 0.5) > 0.05 && noise * (wave + 0.5) < 0.075) ? innerColor : color;
    }

    fragColor = vec4(color, 1.0);
}
