void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.y;
    uv.y += iTime / 4.;

    //var
    float split = 8.;
    float ncol = 3.;
    float degree = 45.;

    vec3 col1 = vec3(255., 255., 0.);
    vec3 col2 = vec3(0., 255., 255.);
    vec3 col3 = vec3(255., 0., 180.);

    // slope
    uv.y -= tan(radians(degree)) * uv.x;

    float id = mod(floor(uv.y * split), ncol);

    vec3 col;
    if(id == 0.){
        col = smoothstep(0., 255., col1);
    }else if(id == 1.){
        col = smoothstep(0., 255., col2);
    }else{
    	col = smoothstep(0., 255., col3);
    }

	// Output to screen
    fragColor = vec4(col,1.0);
}
