#ifdef GL_ES
precision mediump float;
#endif

#define TYPE_1 0
#define TYPE_2 1

#define PATTERN_TYPE TYPE_1

const vec3 BACKGROUND_COLOR = vec3(0.0, 0.4, 0.6);

vec3 hsv(float h, float s, float v)
{
    vec4 t = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(vec3(h) + t.xyz) * 6.0 - vec3(t.w));
    return v * mix(vec3(t.x), clamp(p - vec3(t.x), 0.0, 1.0), s);
}

//Based on:
//https://www.youtube.com/watch?v=-z8zLVFCJv4
//http://www.iquilezles.org/live/index.htm
vec3 leaf(vec2 p, float petals, float scale)
{
	float radius = sqrt(dot(p, p));
	float angle = atan(p.y, p.x);
	float s = 0.5 + 0.5 * sin(petals * angle);
	float t = 0.01 + scale * pow(s, 0.8);
	t += 0.20 * pow(0.5 + 0.5 * cos(12.5 * angle), 1.0);

	float h = radius / t;
    #if PATTERN_TYPE == TYPE_1
	float f = 0.0;
	if (h < 1.0)
	{
	    f = 1.0;
	}
	return mix(vec3(BACKGROUND_COLOR), 
		   vec3(0.5 * h, 0.5 + 0.5 * h, 0.0) + hsv(h * 0.5, 1.0, 0.2), 
		   clamp(sin(h * 3.0), 0.0, 1.0));
    #else
    return mix(vec3(BACKGROUND_COLOR), 
		   	   vec3(0.5 * h, 0.5 + 0.5 * h, 0.0) + hsv(h * 10.5, 0.5, 1.0), 
		   	   clamp(sin(h * 3.0), 0.0, 1.0));
    #endif
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 p = 2.0 * (fragCoord.xy / iResolution.xy) - 1.0;
  	p.x *= iResolution.x / iResolution.y;

	vec3 color = vec3(0.0);
	color += leaf(p, 6.0, 0.9);
	color = pow(color, vec3(0.4545));

	fragColor = vec4(color, 1.0 );
}
