float drawCapsule(vec2 o,vec2 p1,vec2 p2,float r)
{
    vec2 p1o =  o - p1;
    vec2 p1p2 = p2 - p1;

    vec2 p3 = p1 + clamp(dot(p1o,p1p2) / dot(p1p2,p1p2),0.0,1.0) * p1p2; 

    vec2 op3 = p3 - o;

    return length(op3) - r;
}

float drawCicle(vec2 o,vec2 p,float r)
{
    return length(p-o) - r;
}

float drawArc(vec2 o,vec2 p,float r,float d,vec2 right)
{
    vec2 op = p - o;

    float opDeg = acos(dot(normalize(right),normalize(op)));

    return (length(p-o) - r) > 0.0 ? 0.0 : (opDeg < 3.1415926 - d ? 1.0:0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;
	vec2 pos = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 touchPos = (iMouse.xy * 2.0 - iResolution.xy) / iResolution.y;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec2 jointPos1 = texelFetch(iChannel0,ivec2(0,0),0).xy;
    vec2 jointPos2 = texelFetch(iChannel0,ivec2(1,0),0).xy;
    vec2 jointPos3 = texelFetch(iChannel0,ivec2(2,0),0).xy;
    vec2 jointPosAim = texelFetch(iChannel0,ivec2(3,0),0).xy;

    float cicleCol1 = drawCicle(pos,jointPos1,0.07) > 0.0 ? 0.0 : 1.0;
    float cicleCol2 = drawCicle(pos,jointPos2,0.07) > 0.0 ? 0.0 : 1.0;
    float cicleCol3 = drawCicle(pos,jointPos3,0.07) > 0.0 ? 0.0 : 1.0;
    float cicleCol4 = drawCicle(pos,jointPosAim,0.07) > 0.0 ? 0.0 : 1.0;

    vec3 capsuleCol1 = drawCapsule(pos,jointPos1,jointPos2,0.1) > 0.0 ? vec3(1.0) : vec3(0.0);
    vec3 capsuleCol2 = drawCapsule(pos,jointPos2,jointPos3,0.1) > 0.0 ? vec3(1.0) : vec3(0.0);
    vec3 capsuleCol3 = drawCapsule(pos,jointPos3,jointPosAim,0.1) > 0.0 ? vec3(1.0) : vec3(0.0);

    vec3 arcCol = drawArc(pos,jointPosAim,0.2,abs(sin(iTime*2.5)),-normalize(jointPos3-jointPosAim)) > 0.0 ? vec3(0.0) : vec3(1.0);

    col.rgb *= capsuleCol1 * capsuleCol2 * capsuleCol3 * arcCol;

    col.rgb += clamp(cicleCol1 + cicleCol2 + cicleCol3 + cicleCol4,0.0,1.0);

    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 jointPos1 = vec2(-0.6,0.0);
    vec2 jointPos2 = vec2(0.0,0.0);
    vec2 jointPos3 = vec2(0.4,0.0);
    vec2 jointPos4 = vec2(0.8,0.0);

    float defaultLength1 = length(jointPos2 - jointPos1);
    float defaultLength2 = length(jointPos3 - jointPos2);
    float defaultLength3 = length(jointPos4 - jointPos3);

    vec2 touchPos = (iMouse.xy * 2.0 - iResolution.xy) / iResolution.y;

    float time = iTime * 0.25;

    if(iFrame > 0)
    {
        //jointPos1 = texelFetch(iChannel0,ivec2(0,0),0).xy;
        //jointPos2 = texelFetch(iChannel0,ivec2(1,0),0).xy;
        //jointPos3 = texelFetch(iChannel0,ivec2(2,0),0).xy;
        //jointPos4 = texelFetch(iChannel0,ivec2(3,0),0).xy;
        vec2 j2,j3,j4;
        vec2 obv0 = touchPos - jointPos1;    
        float deg01 = atan(-obv0.y,obv0.x);

        for(int i = 0;i <= 100;i ++)
        {
            float deg0 = mix(0.0,deg01,float(i) / 100.0);

            vec2 sc0 = vec2(cos(deg0),sin(deg0));
            mat2 rot0 = mat2(sc0.x,-sc0.y,sc0.y,sc0.x);
            mat2 rot0inv = mat2(sc0.x,sc0.y,-sc0.y,sc0.x);

            vec2 touch2 = rot0inv*(touchPos - jointPos1) + jointPos1;
            j2 = jointPos2;

            vec2  obv = touch2 - j2;
            float oa = defaultLength2;
            float ob = length(obv);
            float ab = defaultLength3;

            float oa2 = oa * oa;
            float ob2 = ob * ob;
            float ab2 = ab * ab;

            float radian = atan(-obv.y,obv.x);

            float degC = 3.1415926-acos(clamp((oa2 + ab2 - ob2) / (2.0*oa*ab),-1.0,1.0));
            float degA = -acos(clamp((oa2 + ob2 - ab2) / (2.0*oa*ob),-1.0,1.0));

            vec2 sc1 = vec2(cos(degA + radian),sin(degA + radian));
            mat2 rot1 = mat2(sc1.x,-sc1.y,sc1.y,sc1.x);

            vec2 sc2 = vec2(cos(degC + degA + radian),sin(degC + degA + radian));
            mat2 rot2 = mat2(sc2.x,-sc2.y,sc2.y,sc2.x);

            j3 = rot1 * (jointPos3 - j2) + j2;
            j4 = rot2 * (jointPos4 - jointPos3) + j2 + j3;

            j3 = rot0 * (j3 - jointPos1) + jointPos1;
            j4 = rot0 * (j4 - jointPos1) + jointPos1;
            j2 = rot0 * (j2 - jointPos1) + jointPos1;

            if(length(j4 - touchPos) < 0.0001)
            {
                break;
            }
        }

        jointPos2 = j2;
        jointPos3 = j3;
        jointPos4 = j4;
    }

    if(int(fragCoord.y) == 0)
    {
        if(int(fragCoord.x) == 0)
        {
            fragColor = vec4(jointPos1,1.0,1.0);
            return;
        }
        else if(int(fragCoord.x) == 1)
        {
            fragColor = vec4(jointPos2,1.0,1.0);
            return;
        }
        else if(int(fragCoord.x) == 2)
        {
            fragColor = vec4(jointPos3,1.0,1.0);
            return;
        }
        else if(int(fragCoord.x) == 3)
        {
            fragColor = vec4(jointPos4,1.0,1.0);
            return;
        }
    }

    fragColor = vec4(0.0,0.0,1.0,1.0);
}
