float opOnion( in float p, in float r )
{
  return abs(p) - r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;
    //Sample a texture
	vec4 col = texture(iChannel0, uv);

    //Some strange bits to make the mouse input work
    vec2 muv = iMouse.xy / iResolution.y;

    //Make an SDF Circle
    float circle = length(uv - muv) - .1;
    //Make it have an outline
    circle = opOnion(circle, .005);

    //Make a color for the cursor
    vec4 cursor = vec4(1) *  step(circle, 0.);

    //Add the cursor on top of the image
    col += cursor;

    // sample texture and output to screen
    fragColor = col;
}
