struct Line {
	vec2 o;
    vec2 dir;
};

vec2 ProjectLineSegment(Line line, vec2 p, float boundLower, float boundUpper) {
	vec2 dir = normalize(line.dir);
    return line.o + dir * clamp(dot(dir, p - line.o), boundLower, boundUpper);
}

vec2 ProjectLine(Line line, vec2 p) {
    vec2 dir = normalize(line.dir);
	return line.o + dir * dot(dir, p - line.o);
}

bool CheckDirection(Line line, vec2 p) {
	return cross(vec3(line.dir, 0.), vec3(line.o - p, 0.)).z < 0.;
}

void ReflectUV(Line line, inout vec2 uv) {
    if (CheckDirection(line, uv)) {
    	vec2 pr = ProjectLine(line, uv);
    	uv = pr + (pr - uv);
    }
}

vec2 NormalizeScreenCoords(vec2 uv, float zoom) {
	uv -= iResolution.xy / 2.;
    uv /= max(iResolution.x, iResolution.y);
    return uv * zoom;
}

void Curve(inout vec2 uv) {

    ReflectUV(Line(vec2(0), vec2(0, 1)), uv);

    float angle = radians(30.);

    Line reflectionLine = Line(vec2(.5, 0), vec2(sin(angle), cos(angle)));

    ReflectUV(reflectionLine, uv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0);

    float z = 6.;

    vec2 uv = NormalizeScreenCoords(fragCoord, z);


    vec4 M = vec4(NormalizeScreenCoords(iMouse.xy, z), NormalizeScreenCoords(iMouse.zw, z));

    uv -= M.xy;

    uv.y -= sin(radians(60.));

    float angle = radians(-120.);

    Line reflectionLine = Line(vec2(1.5, 0), vec2(sin(angle), cos(angle)));

    ReflectUV(reflectionLine, uv);

    angle = radians(300.);

    reflectionLine = Line(vec2(-1.5, 0), vec2(sin(angle), cos(angle)));

    ReflectUV(reflectionLine, uv);

    Curve(uv);

    for (int i = 0; i < 40; i++) {

    	uv.x -= 1.;

    	uv *= 3.;

    	z *= 3.;

    	Curve(uv);
    }

    // Line(M.zw, M.xy - M.zw)

    Line line = Line(vec2(-1.5, 0.), vec2(3., 0.));

    // Time varying pixel color
    col = texture(iChannel0, uv / z).rgb;

    col += vec3(smoothstep(z / max(iResolution.x, iResolution.y), 0., distance(uv, ProjectLineSegment(line, uv, 0., length(line.dir)))));

    // Output to screen
    fragColor = vec4(col,1.0);
}
