// Cross-line anaglyphic training

// Color wheel is palette rainbow for next steps  

const float Pi = 3.1415926;
const float T = 2.;         // period/2
const float N = 20.;         // T*N = max periods

   // for color changing

vec3 Wheel(float WheelPos) {

    if (WheelPos < .33) {
    return vec3 (WheelPos * 3., .0, 1.0 - WheelPos * 3. );
  } else if (WheelPos < .66) {
    WheelPos -= .33;
    return vec3 (1. - WheelPos * 3., WheelPos * 3., .0);
  } else {
    WheelPos -= .66;
    return vec3 (.0, 1.- WheelPos*3., WheelPos*3.);
  }
}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

     // Normalized pixel coordinates 
    vec2 uv = fragCoord/iResolution.xy-.5;  //-0.5  -- 0.5

    vec3 col = vec3 (.0,.0,.0);

    col = Wheel ( mod (.1*iTime,1.));

    vec3 mask = vec3 (.0,.0,.0);
    mask.x = 0.5 + 0.5*cos(N*2.*Pi* abs(mod (iDate.w, T)- T/2.)* uv.x);
    mask.y = mask.z= 0.5 + 0.5*sin(N*Pi* abs(mod (iDate.w, 2.)- 1.)* uv.y);

   //col*= mask;    
   col= mask; 

    // Output to screen
    fragColor = vec4(col, 1.);
}
