vec3 heat(float v)
{    
    vec3 h = vec3(4.0, 2.5, 4.0) * abs(v - vec3(0.75, 0.50, 0.25));
    return 1.0 - h * h;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Output to screen
    fragColor = vec4(heat(uv.x), 1.0);
}
