#define PI 3.14159265

float rewrap(float r, float rep) {

    if (r < 0.) {
        r = -r;
    }

    r = mod(r*rep, PI);

    return r/(PI);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 px = gl_FragCoord.xy / iResolution.xy;

    const int size = 24;

    float ar = iResolution.x / iResolution.y;

    px.y /= ar;

    vec2 pos = px-vec2(0.5, 0.5/ar);

    float len = length(pos);

	vec3 color = vec3(0,0,0);

    float rep = 1.;

    float delta = rewrap(atan(pos.x, pos.y), rep);

    float div = 100./float(size);

    float index = float(int(mod(iTime*0.+delta, 1.) * 100. / div));

	float f = texelFetch(iChannel0, ivec2(index/float(size)*512.,0), 0).x;

    float base = texelFetch(iChannel0, ivec2(0.*512.,0), 0).x;

    float cpi = smoothstep((0.1 + base*0.05), 0.15 + f * 0.01, len);

	if (len < 0.15 + f * 0.1 && len > (0.1 + base*0.05) && mod(delta, 1./float(size)) < 0.035 && delta > 0.005)
		color = vec3(1.-delta, delta*0.3, delta);

    color *= cpi;

    if (len < 0.1 + base * 0.05 && len > 0.09 + base * 0.05)
      color = vec3(1);

    float eq = texelFetch(iChannel0, ivec2(px.x*512.,1), 0).x;

    if (abs(px.y*ar-0.54+eq*0.1) < 0.008 && 
        px.x > 0.5-(0.1+base * 0.05-0.01) &&
        px.x < 0.5+(0.1+base * 0.05-0.01)) {
        color = vec3(eq,1.-eq,eq*eq);
    }

	fragColor = vec4(color, 1);
}
