void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    vec3 col = vec3(0.0);

    float v = texture(iChannel0, vec2(uv.x, 0.0)).x;

	col += pow(1.0 - abs(uv.y - 0.5), clamp(4.0/v, 0.0, 25.0));
    col *= mix(vec3(1.0, 0.5, 0.5), vec3(1.0,2.0,0.5), v);
    col *= col;

    col += texture(iChannel1, uv*0.001+vec2(iTime*0.001,0.0)).xxx*0.25;

    // Output to screen
    fragColor = vec4(col,1.0);
}
