#define BPM 126.5
#define BPS 2.10833
#define PI 3.14159265359

float dist(vec2 uv) {
	return sqrt(pow(abs(uv.x-.5),2.)+ pow(abs(uv.y-.5),2.));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	float d = dist(uv);
	float inp = texture(iChannel0,vec2(1.,1.)).x - iTime/3.;

	vec4 picture = texture(iChannel1, vec2(sin((uv.x-mod(iTime/BPS,3.1)+3.)/6.), 1.-uv.y + .1*sin(uv.x*10.+iTime*BPS*PI)));

	float r = abs(-1.+mod(inp+d,2.));
	float g = abs(-1.+mod(inp+d+1./3.,2.));
	float b = abs(-1.+mod(inp+d+2./3.,2.));
	//fragColor = vec4(inp,inp,inp,1.0);
	float party_grade = .7;
	fragColor = vec4(r*party_grade+picture.r*(1.-party_grade),g*party_grade+picture.g*(1.-party_grade),b*party_grade+picture.b*(1.-party_grade),1.0);
}
