void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float distortion = -0.05 + 0.1*texture(iChannel1, vec2( ((fragCoord.t / iChannelResolution[0].t) + 0.1*iTime) * 0.1, 0.5)).r;
    distortion *= texture(iChannel2, vec2( 0.05*iTime,0.1)).r;
	vec3 offsets = distortion * vec3(0.1,-.02,0);

	float scale = 16.0;
	float scaled_s = ((fragCoord.s / iChannelResolution[0].s) / scale);
	float scaled_t = ((fragCoord.t / iChannelResolution[0].t) / scale);

    vec3 color = 
	   vec3 ( 
		  texture(iChannel0, vec2(scaled_s + offsets.r, scaled_t)).r, 
		  texture(iChannel0, vec2(scaled_s + offsets.g, scaled_t)).g,
		  texture(iChannel0, vec2(scaled_s + offsets.b, scaled_t)).b);
    fragColor = vec4(color,1);
}
