void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2	center = vec2(iResolution.x / 2.0, iResolution.y / 2.0);
	float	dist = distance(fragCoord.xy, center);

	float	radius = 1.0 + 0.8 * iResolution.y * texture(iChannel0, vec2(fragCoord.x / iResolution.x, 0.0)).x;

	if (dist < radius)
	{
		vec3	color = vec3(1.0, 1.0, 1.0);
		float	value = dist / radius;
	    fragColor = vec4((1.0 - value) * color.x, (1.0 - value) * color.y, (1.0 - value) * color.z, 1.0);
	}
	else
	{
		fragColor = vec4(0.0, 0.0, 0.0, 0.0);
	}	
}
