void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float px_x=fragCoord.x;
    float px_y=fragCoord.y;
    float diff=iResolution.x-iResolution.y;
    float r=3.0;
    float x=map(px_x,diff*0.5,iResolution.x-diff*0.5,-r,r);
    float y=map(px_y,0.0,iResolution.y,-r,r);
    float cx=map(iMouse.x,0.0,iResolution.x,-2.0,2.0);
    float cy=map(iMouse.y,0.0,iResolution.y,-2.0,2.0);
    int i=0;

    //(a+bi)/(x+yi)
    //(a+bi)(x-yi)/(x*x+y*y)
    //(a*x+b*x*i-a*y*i+b*y)/(x*x+y*y)
    //(a*x+b*y)/(x*x+y*y) + i(b*x-a*y)/(x*x+y*y)

    for(i=0;i<100&&x*x+y*y<400.0;i++){
        float nx=x*x-y*y-cx;
        float ny=2.0*abs(x*y)-cy;
        x=nx;
        y=ny;

    }
    fragColor=vec4(hsv2rgb(vec3(float(i)/100.0,0.5,1.0)),1.0);
}

float lerp(float t,float a,float b){
    return a+(b-a)*t;
}
float norm(float t,float a,float b){
    return (t-a)/(b-a);
}
float map(float t,float e1,float s1,float e2,float s2){
    return lerp(norm(t,e1,s1),e2,s2);
}
vec3 hsv2rgb(vec3 c) {
  vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
  vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
  return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}
