const float sampleFreq = 0.2;
const float lineWidth = 0.08;


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 samp = texture(iChannel0,vec2(uv.x,sampleFreq));

    if (uv.y < samp.x+lineWidth && uv.y > samp.x-lineWidth) {
        fragColor = vec4(uv.y+(sin(uv.x*100.0)*0.1),0.1,0.1,1.0);
    } else {
        fragColor = vec4(0.0);
    }
}
