// D.Alex
// basic interlace sample code
// dean[ at ]neuroid.co.uk



float x,y;
float screenWidth  = iResolution.x;
float screenHeight = iResolution.y;


void main ( void ){
  y = fragCoord.y / screenHeight;

  if( mod( fragCoord.x, 2.0 ) <= 0.5 ){
    x = fragCoord.x / screenWidth * 0.5;
  }else{
    x = (fragCoord.x + screenWidth) / screenWidth * 0.5;
  }
  fragColor = vec4( texture( iChannel0, vec2( x, y )).xyz, 1.0 );
}
