float noise( in vec3 x ) // From iq
{
    vec3 p = floor(x);
    vec3 f = fract(x);
	f = f*f*(3.0-2.0*f);

	vec2 uv = (p.xy+vec2(37.0,17.0)*p.z) + f.xy;
	vec2 rg = texture( iChannel1, (uv+0.5)/256.0, -100.0 ).yx;
	return mix( rg.x, rg.y, f.z )*2.0 - 1.0;
}

vec2 offset( vec2 p, float phase )
{
	/*return vec2(sin(p.x*29.2532)+cos(p.y*28.4356), 
				  cos(p.y*26.9854)+sin(p.x*27.1084))*0.02*phase;*/
	//return vec2(0.05*phase, 0.0);
	return vec2(noise(vec3(p.xy*10.0,phase)), noise(vec3(p.yx*10.0,phase)))*0.025;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord.xy / iResolution.xy;
	p.x *= iResolution.x / iResolution.y;
	float phase = iTime*0.25;
	vec4 s1 = texture(iChannel0, p + offset(p, fract(phase)), -100.0);
	vec4 s2 = texture(iChannel0, p + offset(p, fract(phase+0.5)), -100.0);
	fragColor = mix(s1, s2, abs(mod(phase*2.0, 2.0)-1.0));
}
