void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.y * 2. - 1.;
	uv.x -= (iResolution.x - iResolution.y) / iResolution.y;

	float power = (sin(iTime * 2.) + 1.) * 5.;
	float level = sin(iTime * 3.)/2. + .5;
	float shadow = (sin(iTime * 5.)/2. + 1.) * 40.;
	vec4 white = vec4(1,1,1,1);
	float r = iResolution.y / 2.;
	float a = sqrt(pow(abs(uv.x), power) + pow(abs(uv.y), power));
	float b = ((level - a) * r + shadow) / shadow;
	float mul = clamp((level - a) * r, 0., 1.);
	fragColor = white * mul + white * (1.-mul) * (1.-b);
}
