void mainImage( out vec4 fragColor, in vec2 fragCoord )
{ 
	vec2 pixel = fragCoord.xy - iResolution.xy*.5;
    float bl = clamp( 2.0*(
                                -0.9*cos(iTime)
                              + (pixel.y / iResolution.y) * cos(iTime*3.7) * 1.5
                              + (pixel.x / iResolution.x) * cos(iTime*5.5)
                           ),
                     0.0, 1.0);

	const float pixelSize = 4.0;
	pixel = pixel/pixelSize;

	vec2 offset = vec2(iTime*3000.0,pow(max(-sin(iTime*.2),.0),2.0)*16000.0)/pixelSize;

	vec3 col;
	for ( int i=0; i < 32; i++ )
	{
		// parallax position
		float depth = 20.0+float(i)/4.0;
		vec2 uv = pixel + offset/depth;

		uv /= iResolution.y;
		uv *= depth/20.0;
		uv *= .4*pixelSize;

		col = mix(texture( iChannel0, uv+.5 ).rgb,
                  texture( iChannel1, uv+.5 ).rgb,
                  bl
                 );

		if ( 1.0-col.y < float(i+1)/32.0 )
		{
			col = mix( vec3(.4,.6,.7), col, exp2(-float(i)*.025) );
			break;
		}
	}

	fragColor = vec4(col,1.0);
}
