void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;


	vec4 color;

	float go = sin(iTime)*0.01;
	float go2 = sin(iTime)*0.01;

	vec2 strenght = vec2(5,2);

	color.r = texture(iChannel0,uv-vec2(go,0.0)*strenght).r;

	color.g = texture(iChannel0,uv-vec2(0.005,go2)*strenght).g;

	color.b=texture(iChannel0,uv).g;
	color.a=1.0;

	fragColor = color;
}
