vec2 rotation(vec2 p, float angle)
{
    float rad = radians(angle);
    return vec2(p.x*cos(rad)+p.y*sin(rad) -cos(rad*0.3)*0.4, 
                -p.x*sin(rad)+p.y*cos(rad) + sin(1.-rad)*.2);
}


float line(vec2 p, vec2 a, vec2 b, float w)
{
     vec2 a2b = b - a;
     vec2 a2p = p - a;     

     // å› ä¸ºæŠ•å½± proj(a2p,a2b) ç­‰äºŽ dot(a2p,a2b) / length(a2b)
     // åœ¨æŠŠæŠ•å½±çš„å€¼å’Œa2bå‘é‡åšæ¯”ä¾‹å…³ç³»ã€‚
     // æ‰€ä»¥proj(a2p,a2b)/length(a2b) ç­‰äºŽ dot(a2p,a2b)/dot(a2b,a2b)

     float h = clamp( dot(a2p,a2b)/dot(a2b,a2b), 0.0, 1.0 );
     vec2 p1 = mix( a, b, h );
     if( length( p1 - p ) <= w )
         return 1.0;

     return 0.0;
}

const vec4 black_color = vec4(0.3, 0.3, 0.3, 1.0);
const vec4 axis_color = vec4(0.0, 0.0, 1.0, 1.0);


void animation(inout vec4 color,vec2 uv)
{
    vec4 newColor = color;
    const float loop = 40.0;
    float time = iTime - 1.0;
    for(float i = 0.0; i < loop; ++i)
    {// ç”»åœ†
		if(line(rotation(uv, (time+i*0.1)* 70.0), 
                		  vec2(0.5,0.0), 
                          vec2(0.5, 0.0), 
                          smoothstep(0.0, 0.2, i/loop*0.2)*0.2
                          /*0.2*/) > 0.0)
    	{
        	newColor = mix(color, vec4(1.0, 0.0, 0.0, 1.0), smoothstep(0.0,loop,i));
    	}
    }

    color = newColor; 
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    // ç”»èƒŒæ™¯
    vec4 color = black_color;

    // ç”»è½´
    if(line(uv, vec2(-2.0, 0.0), vec2(2.0, 0.0), 0.01) > 0.0 ||
      	line(uv, vec2(0.0, -2.0), vec2(0.0, 2.0), 0.01) > 0.0){
        color = axis_color;
    }

	animation(color, uv);

   	fragColor = color;
}
