void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    vec4 col = texture(iChannel0, uv);
    float blue = col.b;
    float red = col.r;

    //float lambda = max(sin(iTime*4.0), 0.0);
    float lambda_2 = max(cos(iTime*30.0), 0.0);

    //float lambda = iMouse.y / iResolution.y;
    //float lambda_2 = iMouse.x / iResolution.x;

    col.b = blue + max(red-blue, 0.0) * lambda_2;
    //col.g = col.g * (1.0-lambda2) + max(red-col.g, 0.0) * lambda;
    //col.g = col.g - red * lambda;
    //col.r = col.r - red * lambda;
    fragColor = vec4(col);
}
