void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

	vec4 base;

	float scaledT = iTime * 5.0;
	//vec2 targ = vec2(sin(scaledT), cos(scaledT));

	//base = vec4(1.0) * float(distance(targ, uv));

	base = texture(iChannel0, uv);

	vec4 color = base;
	// NOTE: The effect works more interestingly with higher maxiters, but long loops tend to crash WebGL
	// or at least be slow. Feel free to increase it, but don't be surprised if it crashes.
	const int maxiters = 100;
	int iters = int(mod(scaledT, float(maxiters)));
	for (int i = 0; i < maxiters; i++)
	{
		color *= 0.8 + (clamp((float(i < iters)) + (1.0/float(iters))  * vec4(vec2(iResolution.x * dFdx(color.rg)), vec2(iResolution.y * dFdy(color.ba)))
					  , 0.0, 1.0) * 0.25);
	}


	fragColor = color;
}
