vec4 fBm(vec2 p) {
	return texture(iChannel0, p)*0.5 + texture(iChannel0, p*vec2(2.0))*0.25 + texture(iChannel0, p*vec2(4.0))*0.125 + texture(iChannel0, p*vec2(8.0))*0.0625;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = fBm((uv.xy*vec2(0.5)*uv.x) + vec2(iTime*0.01, 0.0)) + vec4(0.53, 0.02, 0.02, 0.0);
}
