void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    float ratio = iResolution.x / iResolution.y;
    uv.x *= ratio;

    float movementAnimationSpeed = 1.0;
    float circleSize = 0.2;
    float blendAnimationSpeed = 1.0;
    float pi = 3.14159;
    float sharpness = 60.0;

    vec2 centerOfCircle = vec2(sin(iTime * movementAnimationSpeed)/ 10.0 + 0.5 * ratio,
                                 cos(iTime * movementAnimationSpeed) / 10.0 + 0.5);

    vec2 centerOfCircle2 = vec2(sin((iTime + ((2.0 * pi)/3.0)) * movementAnimationSpeed)/ 10.0 + 0.5 * ratio,
                                 cos((iTime + ((2.0 * pi)/3.0)) * movementAnimationSpeed) / 10.0 + 0.5);

    vec2 centerOfCircle3 = vec2(sin((iTime + ((2.0 * pi)* 2.0 /3.0)) * movementAnimationSpeed)/ 10.0 + 0.5 * ratio,
                                 cos((iTime + ((2.0 * pi)* 2.0/3.0)) * movementAnimationSpeed) / 10.0 + 0.5);

    float distance1 = distance(centerOfCircle, uv) / circleSize;
    float distance2 = distance(centerOfCircle2, uv) / circleSize;
    float distance3 = distance(centerOfCircle3, uv) / circleSize;

    float finalSharpness = sharpness * sin(blendAnimationSpeed * iTime);

    float colR = 1.0 - pow(distance1 * 5.0, finalSharpness);
    float colB = 1.0 - pow(distance2 * 5.0, finalSharpness);
    float colG = 1.0 - pow(distance3 * 5.0, finalSharpness);

    //return color
    fragColor = vec4(colR, colB, colG, 1.0);
}
