#define OUTLINE_COLOR vec4(.2)
#define OUTLINE_STRENGTH 20.
#define OUTLINE_BIAS -.5
#define OUTLINE_POWER 1.
#define PRECISION 8.


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = vec2(fragCoord.x/iResolution.x, fragCoord.y/iResolution.y);
	vec4 ogColor = texture(iChannel0, uv);
    vec4 texColor = ogColor;

    if(texColor.r + texColor.g + texColor.b >= 1.0)
    {
		texColor = vec4(1,1,1,1);
    }
    else
    {
        texColor = vec4(0,0,0,1);
    }
	texColor.b = (uv.x+uv.y)/2.0;



    vec2 r = iResolution.xy;
    vec4 p = pow(texture(iChannel0,fragCoord/r),vec4(2.2)),
        s = pow(texture(iChannel0,(fragCoord+.5)/r),vec4(2.2));
    float l = clamp(pow(length(p-s),OUTLINE_POWER)*OUTLINE_STRENGTH+OUTLINE_BIAS,0.,1.);
    p = floor(pow(p,vec4(1./2.2))*(PRECISION+.999))/PRECISION;
    vec4 texColor2 = mix(p,OUTLINE_COLOR,l);


    fragColor = texColor*texColor2 + texColor2*0.5;
}
