float PI = 3.141592654;

mat2 rotate(float angle) {
    return mat2(cos(angle), -sin(angle),
               	sin(angle), cos(angle));
}

// Thanks FabriceNeyret2

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 st = (fragCoord - .5 * iResolution.xy) / iResolution.y;


    float size =10.;
    float f;


        st *= size;


    st *= cos(iTime) / 2. + .5 + 1.;

   float t1 = sin(iTime) * ceil(10.*sin(length(st)) / 2. + .5);

    st = rotate(t1/5.) * st;
    float p = sin(size*fract(atan(st.x, st.y)))/2. + .5;


    float l = length(st);
    float t = sin(iTime);


    l += smoothstep(.45, 0.5, p)*PI * t;
    f = smoothstep(0.476, -0.096, sin(l)/2. + .5);


    fragColor = vec4(vec3(f),1.0);
}
