#define t iDate.w * 4.
#define V vec3

void mainImage( out vec4 f, in vec2 w ) {
	vec2 p = w / iResolution.xy-.5;
	p.y += (.8 + p.x) * sin(p.x * 8. - t ) * .08;
    V  n= normalize(V(p, 0.1));

	V c = V(.5);
    V s = texture(iChannel0,p + t).rgb;

   if(abs(p.x) < .4 && abs(p.y) < .4)

   {

     c = n * s + .2  ;
     c = pow ( c, s );

   }

	f = vec4(c , 1.);
}
