#define RX (.5*iResolution.x)
#define RY (.5*iResolution.y)
#define A trunc(.1*RY)  // 20.

void mainImage( out vec4 O, in vec2 U){
U= abs(U-.5*iResolution.xy);

vec2 g= mod(U, A);
float o= min(g.x, g.y);
o= min(o, abs(U.x*RY/RX - U.y));
o= min(o, abs(length(U) - RY));
o= min(o, abs(length(U - .4*iResolution.xy) - 2.*A));

O= vec4(1.-o);
}
