const int RAY_STEPS = 256;
const float HALF_PI = 3.14159 * 0.5;

#define LIGHT_POS vec3(0.0, 7.45, 0.0)

#define BACK_WALL 0
#define LEFT_WALL 1
#define RIGHT_WALL 2
#define CEILING 3
#define FLOOR 4
#define TALL_BOX 5
#define SMALL_BOX 6

struct Intersection
{
    float t;
    vec3 color;
    vec3 p;
    int object;
};

vec3 rotateX(vec3 p, float amt) {
    return vec3(p.x, cos(amt) * p.y - sin(amt) * p.z, sin(p.y) + cos(p.z));
}

vec3 rotateY(vec3 p, float a) {
    return vec3(cos(a) * p.x + sin(a) * p.z, p.y, -sin(a) * p.x + cos(a) * p.z);
}

void raycast(vec2 uv, out vec3 dir, out vec3 eye, out vec3 ref) {
    eye = vec3(0.0, 4.0, -20.0);
    ref = vec3(0.0, 1.0, 0.0);

    float len = tan(3.14159 * 0.125) * distance(eye, ref);
    vec3 H = normalize(cross(vec3(0.0, 1.0, 0.0), ref - eye));
    vec3 V = normalize(cross(H, eye - ref));
    V *= len;
    H *= len * iResolution.x / iResolution.y;
    vec3 p = ref + uv.x * H + uv.y * V;
    dir = normalize(p - eye);
}


float box(vec3 p, vec3 b)
{
  return length(max(abs(p) - b, 0.0));
}


float plane( vec3 p, vec4 n )
{
  return dot(p,n.xyz) + n.w;
}


#define BACK_WALL_SDF plane(pos, vec4(0.0, 0.0, -1.0, 5.0))
#define LEFT_WALL_SDF plane(pos, vec4(1.0, 0.0, 0.0, 5.0))
#define RIGHT_WALL_SDF plane(pos, vec4(-1.0, 0.0, 0.0, 5.0))
#define CEILING_SDF plane(pos, vec4(0.0, -1.0, 0.0, 7.5))
#define FLOOR_SDF plane(pos, vec4(0.0, 1.0, 0.0, 2.5))
#define TALL_BOX_SDF box(rotateY(pos + vec3(2, 0, -3), 27.5 * 3.14159 / 180.0), 0.5 * vec3(3.0, 6.0, 3.0))
#define SMALL_BOX_SDF box(rotateY(pos + vec3(-2, 1, -0.75), -17.5 * 3.14159 / 180.0), 0.5 * vec3(3.0, 3.0, 3.0))

float sceneMap3D(vec3 pos)
{
    float t = BACK_WALL_SDF;
    t = min(t, LEFT_WALL_SDF);
    t = min(t, RIGHT_WALL_SDF);
    t = min(t, CEILING_SDF);
    t = min(t, FLOOR_SDF);
    t = min(t, TALL_BOX_SDF);
    t = min(t, SMALL_BOX_SDF);
    return t;
}


void sceneMap3D(vec3 pos, out float t, out int obj)
{
    t = BACK_WALL_SDF;
    float t2;
    obj = BACK_WALL; 
    if((t2 = LEFT_WALL_SDF) < t) {
        t = t2;
        obj = LEFT_WALL; 
    }
    if((t2 = RIGHT_WALL_SDF) < t) {
        t = t2;
        obj = RIGHT_WALL; 
    }
    if((t2 = CEILING_SDF) < t) {
        t = t2;
        obj = CEILING; 
    }
    if((t2 = FLOOR_SDF) < t) {
        t = t2;
        obj = FLOOR; 
    }
    if((t2 = TALL_BOX_SDF) < t) {
        t = t2;
        obj = TALL_BOX; 
    }
    if((t2 = SMALL_BOX_SDF) < t) {
        t = t2;
        obj = SMALL_BOX; 
    }
}

void march(vec3 origin, vec3 dir, out float t, out int hitObj, vec3 lightPos)
{
    t = 0.001;
    for(int i = 0; i < RAY_STEPS; ++i)
    {
        vec3 pos = origin + t * dir;
    	float m;
        sceneMap3D(pos, m, hitObj);
        if(m < 0.01)
        {
            return;
        }
        t += m;
    }
    t = -1.0;
    hitObj = -1;
}

vec3 computeNormal(vec3 pos)
{
    vec3 epsilon = vec3(0.0, 0.001, 0.0);
    return normalize( vec3( sceneMap3D(pos + epsilon.yxx) - sceneMap3D(pos - epsilon.yxx),
                            sceneMap3D(pos + epsilon.xyx) - sceneMap3D(pos - epsilon.xyx),
                            sceneMap3D(pos + epsilon.xxy) - sceneMap3D(pos - epsilon.xxy)));
}

vec3 computeMaterial(int hitObj) {
    float t;
    switch(hitObj) {
        case 0:
        // Back wall
        return vec3(0.8, 0.4, 0.0);
        break;
        case 1:
        // Right wall
        return vec3(0.8, 0.2, 0.2);
        break;
        case 2:
        // Left wall
        return vec3(0.2, 0.8, 0.2);
        break;
        case 3:
        // Ceiling
        return vec3(1.0, 1.0, 1.0);
        break;
        case 4:
        // Floor
        return vec3(0.2, 0.2, 0.6);
        break;
        case 5:
        // Tall box
        return vec3(0.0, 1.0, 1.0);
        break;
        case 6:
        // Small box
        return vec3(0.7, 0.7, 0.0);
        break;
    }
    return vec3(1.0);
}


Intersection sdf3D(vec3 dir, vec3 eye, vec3 lightPos)
{
    float t;
    int hitObj;
    march(eye, dir, t, hitObj, lightPos);

    if(t == -1.0) {
        return Intersection(t, vec3(0.0, 0.0, 0.0), vec3(eye + 1000.0 * dir), -1);
    }

    vec3 isect = eye + t * dir;
    vec3 nor = computeNormal(isect);
    vec3 lightDir = normalize(lightPos - isect);

    vec3 material = computeMaterial(hitObj);

    float lambert = dot(normalize(lightPos - isect), nor) + 0.5;

    vec3 sdfColor = material * lambert;

    return Intersection(t, sdfColor, isect, hitObj);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 uv2 = 2.0 * uv - vec2(1.0);


    Intersection aaIsects[16];
    vec3 dir, eye, ref;
    int idx = 0;
    for(float i = 0.0; i < 1.0; i += 0.25) {
        for(float j = 0.0; j < 1.0; j += 0.25) {
            raycast(uv2 + vec2(i, j) / iResolution.xy, dir, eye, ref);
            aaIsects[idx++] = sdf3D(dir, eye, LIGHT_POS);
        }
    }
    vec3 avgColor = vec3(0.0);
    for(int i = 0; i < 16; ++i) {
        avgColor += aaIsects[i].color;
    }
    avgColor *= 0.0625;

    // Output to screen
    fragColor = vec4(avgColor, 1.0);
}
