#define MAX_STEPS 120
#define MAX_DIST 100.
#define SURF_DIST .0001

#define PI 3.1415
#define SPEED 1.0


struct RayMarchObject 
{
    int materialID;
    float dist;
};

struct Material 
{
    vec3 albedo;
    float emissiveness;
};

const int materialCount = 4;
Material[materialCount] materials = Material[materialCount](
    Material(vec3(1.0), 0.0),
    Material(vec3(0.09, 0.75, 0.09), 0.0),
    Material(vec3(1.0, 0.09, 0.09), 0.0),
    Material(vec3(1.0), 1.0)
);

mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

RayMarchObject GetScene(vec3 p) 
{
    p.xz = Rot(0.3) * p.xz;
    float distBox = sdBox(p + vec3(0.03, 0.02, -0.04), vec3(0.03, 0.07, 0.03));
    RayMarchObject box = RayMarchObject(0, distBox);
    p.xz = Rot(-0.3) * p.xz;

    p.xz = Rot(-0.3) * p.xz;
    float distCube = sdBox(p - vec3(0.04, -0.05, -0.01), vec3(0.025));
    RayMarchObject cube = RayMarchObject(0, distCube);
    p.xz = Rot(0.3) * p.xz;

    float greenBoxDist = sdBox(p + vec3(0.1, -0.025, 0.0), vec3(0.002, 0.1, 0.1));
    RayMarchObject greenBox = RayMarchObject(1, greenBoxDist);

    float redBoxDist = sdBox(p - vec3(0.1, 0.025, 0.0), vec3(0.002, 0.1, 0.1));
    RayMarchObject redBox = RayMarchObject(2, redBoxDist);

    float groundBoxDist = sdBox(p - vec3(0.0, -0.075, 0.0), vec3(0.1, 0.002, 0.1));
    RayMarchObject groundBox = RayMarchObject(0, groundBoxDist);

    float topBoxDist = sdBox(p - vec3(0.0, 0.125, 0.0), vec3(0.1, 0.002, 0.1));
    RayMarchObject topBox = RayMarchObject(0, topBoxDist);

    float backBoxDist = sdBox(p - vec3(0.0, 0.025, 0.1), vec3(0.1, 0.1, 0.002));
    RayMarchObject backBox = RayMarchObject(0, backBoxDist);

    float lampDist = sdBox(p - vec3(0.0, 0.123, 0.0), vec3(0.035, 0.002, 0.035));
    RayMarchObject lamp = RayMarchObject(3, lampDist);

    const int objectCount = 8;
    RayMarchObject[objectCount] allObjects = RayMarchObject[objectCount](
        box, cube, greenBox, redBox, groundBox, backBox, topBox, lamp
    );

    //////////////////////////////////////////

    RayMarchObject finalObject = allObjects[0];

    for (int i = 0; i < objectCount - 1; i++) {
        RayMarchObject currentObj = allObjects[i + 1];
        if(currentObj.dist < finalObject.dist) finalObject = currentObj;
    }

    return finalObject;
}

RayMarchObject RayMarch(vec3 ro, vec3 rd) 
{
	float dO=0.;
    int materialID;
    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        RayMarchObject object = GetScene(p);
        float dS = object.dist;
        dO += dS;
        if(dO>MAX_DIST || abs(dS)<SURF_DIST) {
            materialID = object.materialID;
            break;
        }
    }

    return RayMarchObject(materialID, dO);
}

vec3 GetNormal(vec3 p) {
	float d = GetScene(p).dist;
    vec2 e = vec2(.0001, 0);

    vec3 n = d - vec3(
        GetScene(p - e.xyy).dist,
        GetScene(p - e.yxy).dist,
        GetScene(p - e.yyx).dist);

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	vec2 m = iMouse.xy/iResolution.xy;

    vec3 ro = vec3(0.0, 0.015, -0.13) * 2.2;

    if(m.xy == vec2(0.0)) {
        m = vec2(0.56, 0.6);
    }

    vec3 rd = GetRayDir(uv, ro, vec3(0,0.02,0), 1.);
    vec3 col = vec3(0);

    RayMarchObject scene = RayMarch(ro, rd);

    if(scene.dist < MAX_DIST) 
    {
        vec3 p = ro + rd * scene.dist;
        vec3 n = GetNormal(p);
        vec3 r = reflect(rd, n);
        vec3 lightVec = normalize(vec3(1.0, 0.4, -0.2));
        float NdotL = max(dot(n, lightVec), 0.0);

        col = vec3(mix(NdotL, 1.0, 0.3)) * materials[scene.materialID].albedo;
    }

    vec2 st = fragCoord / iResolution.xy;
    col *= mix(sin(st.x * PI) * sin(st.y * PI), 1.0, 0.2);    // Vignette

    col = pow(col, vec3(1.0 / 2.2));

    fragColor = vec4(col,1.0);
}
