void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col;
    float t = iTime;
    vec2 uv = (fragCoord*10.0-iResolution.xy)/iResolution.y/10.0;
    uv += vec2(t/2.0,t/3.0)/4.0;
    for(int c=0;c<3;c++){
        float scale = 5.0;
        float scale1 = 1.5;
        float s1 = scale1*scale;
        for(int i=0;i<6;i++)
        {
            uv += uv.yy;
            uv = fract((uv)/s1)*s1;
            uv=-fract(uv/(4.0-col.x-abs((uv.y+uv.x)/(16.0)))-(uv/(2.5+(fract(uv.x+uv.y))))/scale)*scale/scale1+s1;
            uv.y /= scale1+col.x;
        	uv=uv.yx+col.xy;
            uv.x /= -(1.0+col.x/scale);
            col[c] = fract((((1.0+col.y)*.25)*(col.x+uv.y-uv.x))/2.0);
        }
	}
    fragColor = vec4(vec3(col),1.0);

}
