vec2 uv;
float pi = 3.1415926536;

float sine(float seed, float freq, float amp, vec2 pos) {
    return sin(seed*freq+pos.x)*amp+pos.y;
}

void aspectNorm(vec2 fragCoord, vec2 scale) {
    if (iResolution.x<iResolution.y) {
        uv = scale*(fragCoord/iResolution.xx-vec2(0.5, iResolution.y/iResolution.x/2.0));
    } else {
        uv = scale*(fragCoord/iResolution.yy-vec2(iResolution.x/iResolution.y/2.0, 0.5));
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    aspectNorm(fragCoord, vec2(1.0, 1.0));

    vec3 col;
    float saveSine = sine(uv.x, 5.0, 0.25, vec2(iTime, 0.0));
    saveSine += sine(uv.x, 16.0, 0.125, vec2(iTime/3.0*2.0, 0.0));
    saveSine += sine(uv.x, 21.0, 0.0625, vec2(iTime/3.0, 0.0));
    if (saveSine>uv.y) {
        col = vec3(uv.y+1.0-saveSine);
    } else {
        col = vec3((saveSine)*2.0+1.0-uv.y*2.0);
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
