    float blink=0.5;
    float bar_h = 64.;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float time = iTime;

    float v_sample = time*blink;;

    for(float i=0.0;i<32.0;i++)
    {

    if(time>i) bar_h=64.-(i*2.0)+1.0;


    }


    vec2 uv  = fragCoord.xy / iResolution.xy;
    uv.x = v_sample;
    uv-= mod(uv,vec2(1.0)/(iChannelResolution[0].xy*10./bar_h));


    fragColor = texture(iChannel0, uv/2.);
}
