// 
//***** BELGIAN FLAG ***** 
// by Yakoudbz
//

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	float siny = sin(fragCoord.y/iResolution.y*15.+iTime*7.);
	float x = fragCoord.x/iResolution.x -siny/300.;

	float red = smoothstep(.3,.31,x);
    float green = min(red,smoothstep(.7,.69,x));

	fragColor = vec4( red*.7 ,green*.5, .0, 1. )-siny/20.;

}
