void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Coordinate system
	vec2 p = fragCoord.xy / iResolution.xy;

    // Pixel size
    float PXSIZE = 1./iResolution.x;

    // Variables to store unsigned distance value and anti-aliasing factor
    float udCurve, aaf;

    // Render curve using pixelsize-based anti-aliasing
    udCurve = abs(p.y - sin(p.x));
    aaf = 3. * PXSIZE;
	fragColor = mix(vec4(1), vec4(0), smoothstep(0., aaf, udCurve));

    // Render curve using fwidth-based anti-aliasing
    udCurve = p.y - sin(p.x) + .2;
    aaf = fwidth(udCurve); // pre-discontinuaty metric
    udCurve = abs(udCurve); // as proposed by FabriceNeyret2
	fragColor += mix(vec4(1), vec4(0), smoothstep(0., aaf, udCurve));
}
