// City skyline
// I just picked this off the Sandbox thing and made it smaller and gave it a hazy sunset...
// http://glslsandbox.com/e#22564.0

void mainImage( out vec4 r, in vec2 c )
{
	vec2 p = ( c.xy / iResolution.xy );
	float  a = 0.;
	for (float f = 1.; f < 35.; f++) 
	a = p.y + f*0.017 < sin(floor(5e2*p.x/f+99.*f+iDate.w)) ? f / 32.:a; 
	r = vec4(mix(vec3(0), vec3(1.0-.1*p.y, .6*p.y,.2)+p.x*.3, 1.-a), 1.);
}
