void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float m = 0.5;
	float d1 = sqrt( pow(abs(uv[0]-0.5), 1.25) + pow(abs(uv[1]-0.25), 1.75) );
	float d2 = sqrt( pow(abs(uv[0]-0.5), 1.25) + pow(abs(uv[1]-0.75), 1.75) );    
	float c1 = sin(d1*iTime*m) * cos(d2*iTime*m) * sin(uv[0]*iTime); 
    float c2 = sin(d1*iTime*m) * cos(d2*iTime*m) * sin(uv[1]*iTime);

    fragColor = vec4(c1, c2, c2,1.0);
}
