float scale=2.0;

float triangle(vec2 z)
{
	vec2 a1 = vec2( 0.0, 1.0);
	vec2 a2 = vec2(0.7071,  -0.7071);
	vec2 a3 = vec2(-0.7071, -0.7071);

	vec2 c;
	float dist, d;
	for(int n = 0; n < 40; ++n) {
        c=a1;
        dist=length(z-a1);

        d=length(z-a2);
        if(d<dist){
            c=a2;
            dist=d;
        }

        d=length(z-a3);
        if(d<dist){
            c=a3;
            dist=d;
        }
        z=scale*z-(scale-1.0)*c;

    }

	return length(z);// * pow(Scale, float(-Iterations));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	// Scale uv to -1..1 range
    uv = 2.0 * uv - 1.0;
    uv.y += 0.1;

    float d = triangle(uv);

   	// Convert distance d to color
    //vec3 color = mix(vec3(0.0), vec3(1.0), step(Eps, d));

    // Use d as color directly
    vec3 color = vec3(d*0.000000001);

    fragColor = vec4(color, 1.0);
}
