void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime+100.0;
	float amp = texture(iChannel0, vec2(0.1, 0.0)).x ;

    vec2 uv = -0.5 + fragCoord.xy / iResolution.xy ;
    uv.x *= iResolution.x / iResolution.y ;

    vec3 lightDirection = normalize(vec3(sin(amp * 10.0), cos(amp * 10.0), sin(amp*10.0)));
    vec3 color = vec3(0.0, 0.0, 0.0);
    float sphereRadius = 1.0;
    vec3 spherePosition = vec3(0.0, 0.0, 0.0);

    vec3 ro = vec3(0.0, 0.0, -5.0);
    vec3 rd = normalize(vec3(uv, -2.0) - ro);
    float dist;
    for (int i = 0; i < 200; i++) {
        float index = float(i) / 200.0;
        float amp = texture(iChannel0, vec2(index, 0.0)).x ;    
        dist = distance(ro, spherePosition) - sphereRadius * amp;
        //ro += mix(cos(105.0*rd+t), sin(40.0*amp*rd+t), sin(t));
        ro += mix(cos(rd*t), sin(rd), sin(t + 100.0) * amp);
        if ( dist <  0.001 ) {
            vec3 normal = normalize(spherePosition - ro);
            rd = reflect(rd, normal);
            color = vec3(uv * 4.0, 0.1) * dot(lightDirection, rd);
            break;   
        }
        ro += rd * dist * 0.95;   
    }
    if (dist > 0.001) {
        //Change background
        //color = texture(iChannel2, uv).rgb;
    }
    fragColor = vec4(color, 1.0);
