// mathematical constants
const float pi = 3.1415926535897932384626433832795;
// translate distance function
vec2 translate(vec2 p, vec2 offset)
{
    return p - offset;
}
// rotate distance function
vec2 rotate(vec2 p, float angle)
{
    float c = cos(angle);
    float s = sin(angle);
    mat2 R = mat2(c, -s, s, c);
    return R * p;
}
// repeat with rotational symmetry
vec2 mirror(vec2 p, float degree)
{
    const float tau = pi * 2.0;
    const float piHalf = 1.57079632679489661923;
    float angle = tau / degree;
    float theta = atan(p.y, p.x) + piHalf + angle / 2.0;
    float turn = floor(theta / angle) * angle;
    return rotate(p, turn);
}
// draw antialiased point with minimum pixel size 
vec3 draw(in vec3 buffer, in float d, in float r, in vec3 color)
{
    float up = min(iResolution.x,iResolution.y);
    d = up * d;
    r = r * up / 360.0;
    r = clamp(r, 1.5, 10000.0);
  	float aa = fwidth(d);
   	return mix(buffer, color, 1.0 - smoothstep(r - aa, r + aa / 2.0, d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // normalize pixel coordinates centered at origin
    vec2 coord = fragCoord - iResolution.xy / 2.0;
    vec2 uv = coord / min(iResolution.x, iResolution.y);
    // periodic time varying pixel color
    vec3 col = 0.5 + 0.5 * cos(iTime + uv.xyx + vec3(0, 2, 4));
    // geometry: the golden ratios of the pentagram/pentagon
    const float r = 0.5;
    const float phi	= (sqrt(5.0) + 1.0) / 2.0;
    const float phiSquared = phi * phi;
    const float rSmaller = r / phiSquared;
    const float a = r * phi / 2.0;
    const float h = a - rSmaller;
    const float w = r * sqrt((sqrt(5.0) + 5.0) / 2.0) / phi;
    // zoom
    const float phiToThe4th = phiSquared * phiSquared;
	const float logPhiSquared = log(phiSquared);
    float t = 1.0 + mod(iTime, phiToThe4th - 1.0);
    float u = 0.5 * log(t) / logPhiSquared;
    float z = mix(1.0, 1.0 / phiToThe4th, u);
    vec2 q = uv * z;
    // fractalize
    float l = length(q);
    float n = floor(log(r / l) / logPhiSquared);
    float s = pow(phi, 2.0 * n);
    // flip & spin
    q = rotate(q, pi * n - iTime / 8.0);
    // 5-way symmetry
    q = mirror(q, 5.0);
    // scale
    q = q * s;
    // position pentagram at height appropriate for level in the fractal
    q = translate(q, vec2(0.0, -rSmaller));
    // calculate distance from oriented base triangle
    float d = sdTriangleIsosceles(vec2(w / 2.0, -h), q) / (s * z);
	// render anti-aliased lines scaled to position in fractal
    float b = length(uv);
    col = draw(vec3(0.0), abs(d), 8.0 * b, 4.0 * sqrt(b) * col);
    // output to screen
    fragColor = vec4(col, 1.0);
}

/// Inigo Quilez's signed distance to a 2D triangle
// reference Demo: https://www.shadertoy.com/view/MldcD7
float sdTriangleIsosceles( in vec2 q, in vec2 p )
{
    p.x = abs(p.x);

	vec2 a = p - q*clamp( dot(p,q)/dot(q,q), 0.0, 1.0 );
    vec2 b = p - q*vec2( clamp( p.x/q.x, 0.0, 1.0 ), 1.0 );

    float s = -sign( q.y );

    vec2 d = min( vec2( dot( a, a ), s*(p.x*q.y-p.y*q.x) ),
                  vec2( dot( b, b ), s*(p.y-q.y)  ));

	return -sqrt(d.x)*sign(d.y);
}
