// @FabriceNeyret2 shorter variant
void mainImage( out vec4 O,  vec2 U )
{
  vec2 R = iResolution.xy, m = iMouse.xy / R;
  O = vec4(U / R.y, 1.5-m.x+.04*sin(iDate.w),0);
  for (int i = 0; i < 88; i++)
    O.xzy = vec3(1.3, 1, .777) * abs(O.xyz/dot(O,O)-vec3(1,1,m.y*.3));
}
