float heart(float theta) {
	return 2. - 2.*sin(theta) + sqrt(abs(cos(theta)))*sin(theta)/(1.4 + sin(theta));
}

float pulse(float time) {
    float A = -2.;
    float T = 1.5;
    time = mod(time, T);

    if (time < T/2.)
        return A*mix(1., 0.8, (T/2.-time)/(T/2.));
    else
        return A*mix(.8,1.1,(T-time)/(T/2.));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 point = fragCoord.xy / iResolution.y - vec2(0.5*iResolution.x/iResolution.y, 0.8);

    float p = pulse(iTime);
    if (length(point) < heart(atan(point.y,point.x))/(8. + p)) {
        fragColor = vec4(1.,0.,-p/4.,1.);
    } else {
        fragColor = vec4(0.);
    }
}
