#define TIME iTime
#define TEX iChannel0

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = (2.0*fragCoord.xy - iResolution.xy)/iResolution.y;
	vec3 col = vec3(0.0);

	uv += (texture(TEX,vec2(uv.y,TIME*0.5)).xy-0.5)*smoothstep(1.5,0.0,mod(TIME,5.0));
	uv += (texture(TEX,vec2(TIME*0.5,uv.x)).xy-0.5)*smoothstep(1.5,0.0,mod(TIME,4.0));

	uv.x += sin(uv.y*2.0+TIME/1.5)*0.075;
	uv.y += cos(uv.x*3.0-TIME/2.0)*0.075;
	col.r = smoothstep(1.0,0.5,length(uv));

    uv += (texture(TEX,vec2(uv.y,TIME*0.5)).xy-0.5)*smoothstep(1.5,0.0,mod(TIME,5.0));
	uv += (texture(TEX,vec2(TIME*0.5,uv.x)).xy-0.5)*smoothstep(1.5,0.0,mod(TIME,4.0));

	uv.x -= sin(uv.y*3.0-TIME/2.0)*0.075;
	uv.y += cos(uv.x*2.0+TIME/1.5)*0.075;
	col.g = smoothstep(1.0,0.5,length(uv));

    uv += (texture(TEX,vec2(uv.y,TIME*0.5)).xy-0.5)*smoothstep(1.5,0.0,mod(TIME,5.0));
	uv += (texture(TEX,vec2(TIME*0.5,uv.x)).xy-0.5)*smoothstep(1.5,0.0,mod(TIME,4.0));

	uv.x += cos(uv.y*2.0+TIME/3.0)*0.075;
	uv.y -= sin(uv.x*3.0+TIME/2.0)*0.075;
	col.b = smoothstep(1.0,0.5,length(uv));

	fragColor = vec4(col,1.0);
}
