vec4 HueShift (in vec3 Color, in float Shift)
{
    vec3 P = vec3(0.55735)*dot(vec3(0.55735),Color);

    vec3 U = Color-P;

    vec3 V = cross(vec3(0.55735),U);    

    Color = U*cos(Shift*6.2832) + V*sin(Shift*6.2832) + P;

    return vec4(Color,1.0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy/-iResolution.xy+1.0;

    vec4 Hue = HueShift(vec3(1.0,0.0,0.0),-uv.x);;

    vec4 Color = HueShift(texture(iChannel0,uv).xyz,iMouse.x/iResolution.x);

	fragColor = vec4(mix(Color,Hue*clamp(abs(fragCoord.x-iMouse.x)/4.0,0.0,1.0),floor(uv.y+0.125)));
}
