void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float x= iResolution.x/10.0;
 	if (fragCoord,iMouse.x!=0.0)
       x=iMouse.x;
    vec2 modul = mod(fragCoord,x/12.0);
	vec2 normalizedCord= vec2(fragCoord-modul)/iResolution.xy;
	fragColor =texture(iChannel2, normalizedCord);
}
